package sfs2x.extension.realpokerserver.src.sfsHandler;

import casino.cardgame.utils.GlobalValue;
import casino.cardgame.utils.Logger;
import com.smartfoxserver.v2.core.ISFSEvent;
import com.smartfoxserver.v2.extensions.BaseServerEventHandler;

public class USER_DISCONNECT extends BaseServerEventHandler {

    @Override
    public void handleServerEvent(ISFSEvent isfse) {
        try {
            Logger.trace("Enter user_disconnect Event");
            GlobalValue.serverHandler.HandleUserDisconnect(isfse);

        } catch (Exception ex) {
            Logger.error(this.getClass(), ex);
        }
    }
}
